import{_ as i,c as s,o as a,ah as t}from"./chunks/framework.HzbgFGQH.js";const g=JSON.parse('{"title":"Shebang","description":"","frontmatter":{},"headers":[],"relativePath":"linux/getting-started/env/shebang.md","filePath":"en/linux/getting-started/env/shebang.md","lastUpdated":1746885209000}'),n={name:"linux/getting-started/env/shebang.md"};function h(l,e,r,o,d,p){return a(),s("div",null,e[0]||(e[0]=[t(`<h1 id="shebang" tabindex="-1">Shebang <a class="header-anchor" href="#shebang" aria-label="Permalink to “Shebang”">​</a></h1><p>Shebang is a special symbol in Unix and Unix-like operating systems, composed of the characters <code>#!</code>, used to indicate which interpreter should execute a script file. It usually appears at the beginning of a script file, followed by the path of the interpreter. Shebang allows script files to be run directly like executable programs, without manually calling the interpreter.</p><h2 id="origin-of-the-name" tabindex="-1">Origin of the Name <a class="header-anchor" href="#origin-of-the-name" aria-label="Permalink to “Origin of the Name”">​</a></h2><p><code>#</code> in English represents hash or sharp, <code>!</code> in programming and command lines represents bang, and the combination of these two symbols is called hash-bang or sharp-bang. Over time, hash-bang was simplified to shebang.</p><h2 id="specifying-the-interpreter" tabindex="-1">Specifying the Interpreter <a class="header-anchor" href="#specifying-the-interpreter" aria-label="Permalink to “Specifying the Interpreter”">​</a></h2><p>The path specified after Shebang is the location of the interpreter, such as:</p><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">#!/bin/bash</span></span></code></pre></div><p>The system will use the bash under this path to execute the script content.</p><h2 id="unified-execution-form" tabindex="-1">Unified Execution Form <a class="header-anchor" href="#unified-execution-form" aria-label="Permalink to “Unified Execution Form”">​</a></h2><p>Scripts of different types can be uniformly executed using the form <code>./file</code>, without explicitly calling the interpreter.</p><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">#!/bin/bash</span></span></code></pre></div><p>No need to use <code>bash file</code> to execute, directly use <code>./file</code>.</p><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">#!/bin/python3</span></span></code></pre></div><p>No need to use <code>python file</code> to execute, directly use <code>./file</code>.</p><h2 id="common-examples" tabindex="-1">Common Examples <a class="header-anchor" href="#common-examples" aria-label="Permalink to “Common Examples”">​</a></h2><p>We often see the following examples in bash scripts.</p><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">#!/usr/bin/env bash</span></span></code></pre></div><p><code>env</code> is a command related to environment variables. When the argument following it is <code>bash</code>, it will search for the bash interpreter based on the path provided by the environment variable <code>PATH</code>. The advantage of doing this is that there is no need to hard-code the path of the bash interpreter, because the bash interpreter may be in different locations in different systems.</p><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">type</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -a</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> bash</span></span></code></pre></div><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">bash</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> is</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /usr/bin/bash</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">bash</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> is</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /bin/bash</span></span></code></pre></div><p>For the above python example, a better Shebang is to use env.</p><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">#!/usr/bin/env python</span></span></code></pre></div><h2 id="manual-invocation" tabindex="-1">Manual Invocation <a class="header-anchor" href="#manual-invocation" aria-label="Permalink to “Manual Invocation”">​</a></h2><p>If there is no Shebang, the interpreter can be manually called.</p><div class="language-bash"><button title="Copy code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">bash</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> script.sh</span></span></code></pre></div>`,25)]))}const b=i(n,[["render",h]]);export{g as __pageData,b as default};
